-- ----------------------------
-- Table structure for user
-- ----------------------------
DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `id` bigint(6) unsigned zerofill NOT NULL AUTO_INCREMENT,
  `parent_id` bigint(6) unsigned zerofill DEFAULT NULL COMMENT '推荐人id',
  `user_name` varchar(200) NOT NULL COMMENT '用户名称/手机号码,唯一',
  `login_pass` varchar(200) NOT NULL COMMENT '登录密码',
  `trade_pass` varchar(200) NOT NULL COMMENT '交易密码',
  `package_code` varchar(300) NOT NULL COMMENT '钱包地址,唯一',
  `credit_card` varchar(100) DEFAULT NULL COMMENT '身份证',
  `icon` varchar(200) DEFAULT NULL COMMENT '头像',
  `wechat` varchar(200) DEFAULT NULL COMMENT '微信',
  `alipay` varchar(200) DEFAULT NULL COMMENT '支付宝',
  `nick_name` varchar(100) DEFAULT NULL COMMENT '昵称,唯一',
  `real_name` varchar(100) DEFAULT NULL COMMENT '真实姓名',
  `level` tinyint(4) NOT NULL COMMENT '0是普通用户/1是代理商',
  `sign` varchar(500) DEFAULT NULL COMMENT '个性签名',
  `create_time` datetime NOT NULL COMMENT '注册的时间',
  `update_time` datetime DEFAULT NULL,
  `trade_count` int(11) NOT NULL DEFAULT '0' COMMENT '交易次数',
  `star_count` int(11) NOT NULL COMMENT '评价星数',
  `trust_count` int(11) NOT NULL COMMENT '信任数量',
  `dic_num` double NOT NULL COMMENT 'dic数量',
  `dio_num` double NOT NULL COMMENT 'dio数量',
  `dog_coin_num` double NOT NULL COMMENT '狗狗币数量',
  `freeze_num` double NOT NULL COMMENT '冻结数量',
  `is_auth` int(11) NOT NULL COMMENT '是否实名认证',
  `invite_code` int(6) unsigned zerofill DEFAULT NULL COMMENT '邀请码（唯一）/废弃。使用主键代替',
  `state` tinyint(4) NOT NULL COMMENT '状态，默认1正常，0是锁定',
  `bank_card_num` varchar(100) DEFAULT NULL,
  `bank_card_type` tinyint(4) DEFAULT NULL COMMENT '0是借记卡',
  `bank_name` varchar(100) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=53 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of user
-- ----------------------------

